initial_chare = 100
def fine_calc(basic_charge,nofdays):
    if(nofdays<7):
        print(f"For {nofdays} Only basic charge applicable:: {basic_charge}.")
    elif(nofdays<15):
        print(f"(For {nofdays} days 10% Charge is applicable::\nFinal amount::{basic_charge+(basic_charge*0.1)}")
    elif(nofdays<30):
        print(f"For {nofdays} days 20% Charge is applicable::\nFinal amount:: {basic_charge+(basic_charge*0.2)}")
    else:
        print(f"For {nofdays} days 30% charge is applicable::\nFinal amount:: {basic_charge+(basic_charge*0.3)}")
nofdays = int(input("Enter no of days:: "))
fine_calc(initial_chare,nofdays)

try:
    while(True):
        a = float(input("Enter num1: "))
        b = (float(input("Enter num2: ")))
        choice = input("Choose operation (+, -, *, /, Y for exit)\n::")
        if(choice=="+"):
            print(a+b)
        elif(choice=="-"):
            print(a-b)
        elif(choice=="*"):
            print(a*b)
        elif(choice=="/"):
            if(b==0):
                print("Zero devision Not Allowed!")
            print(a/b)
        elif choice.upper()=="Y":
            print("Exited Succesfully!")
            break
        else:
            print("Wrong operator try again!")

except Exception as e:
    print(f"Exception Caught:: {e}")
